DELIMITER $$ CREATE FUNCTION exibir_quantidade_pessoas_contratadas_por_mes_e_ano(`monthOfStart` INT, `yearOfStart` INT) RETURNS INT READS SQL DATA BEGIN DECLARE numberOfHiredPeople INT; SELECT COUNT(*) FROM hr.employees AS E  WHERE MONTH(E.HIRE_DATE) = `monthOfStart` AND YEAR(E.HIRE_DATE) = `yearOfStart` INTO numberOfHiredPeople; RETURN numberOfHiredPeople; END $$ DELIMITER ;
