SELECT JOB_TITLE AS 'Cargo', CASE WHEN 5000 AND 10000 BETWEEN MIN_SALARY AND MAX_SALARY THEN 'Baixo' WHEN 10001 AND 20000 BETWEEN MIN_SALARY AND MAX_SALARY THEN 'Médio' WHEN 20001 AND 30000 BETWEEN MIN_SALARY AND MAX_SALARY THEN 'Alto' ELSE 'Altíssimo' END AS 'Nível' FROM hr.jobs ORDER BY JOB_TITLE ASC;