SELECT j.JOB_TITLE AS 'Cargo', round(avg(e.SALARY), 2) AS 'Média salarial', CASE WHEN round(avg(e.SALARY), 2) BETWEEN 2000 AND 5800 THEN 'Júnior' WHEN round(avg(e.SALARY), 2) BETWEEN 5801 and 7500 then 'Pleno' when round(avg(e.SALARY), 2) between 7501 and 10500 then 'Sênior' when round(avg(e.SALARY), 2) > 10500 then 'CEO' else null end as 'Senioridade' from hr.employees AS e INNER JOIN hr.jobs AS j ON e.JOB_ID = j.JOB_ID GROUP BY Cargo ORDER BY `Média salarial`, j.JOB_TITLE ;