SELECT JOB_TITLE AS 'Cargo', (SELECT ROUND(AVG(SALARY), 2) FROM hr.employees WHERE JOB_ID = hr.jobs.JOB_ID) AS `Média salarial`, ( SELECT CASE WHEN ROUND(AVG(SALARY), 2) <= 5800 THEN 'Júnior' WHEN ROUND(AVG(SALARY), 2) <= 7500 THEN 'Pleno' WHEN ROUND(AVG(SALARY), 2) <= 10500 THEN 'Sênior' ELSE 'CEO' END AS 'Senioridade' FROM hr.employees WHERE JOB_ID = hr.jobs.JOB_ID ) AS 'Senioridade' FROM hr.jobs ORDER BY `Média salarial`, JOB_TITLE;
