SELECT t1.ContactName AS 'Nome de contato', (SELECT ShipperName FROM w3schools.shippers WHERE w3schools.shippers.ShipperID = t2.ShipperID) AS `Empresa que fez o envio`, t2.OrderDate AS 'Data do pedido' FROM w3schools.customers AS t1 INNER JOIN w3schools.orders AS t2 ON t1.CustomerID = t2.CustomerID WHERE t2.ShipperID IN (1,2) ORDER BY t1.ContactName, `Empresa que fez o envio`, t2.OrderDate;
